import React from "react";
import AppSearchAPIConnector from "@elastic/search-ui-app-search-connector";
import {
    PagingInfo,
    ResultsPerPage,
    Paging,
    SearchProvider,
    Results,
    SearchBox
} from "@elastic/react-search-ui";
import {Layout} from "@elastic/react-search-ui-views";
import "@elastic/react-search-ui-views/lib/styles/styles.css";

// ============== retrieving credentials from my account ==============
const connector = new AppSearchAPIConnector({searchKey: "search-j2upnixcxa1k28efrs8sugrv", engineName: "play-search", endpointBase: "https://play-search.ent.us-central1.gcp.cloud.es.io", cacheResponses: false});



// ============== setting configurations for connection and searches ==============

const config = {
    apiConnector: connector,

    searchQuery: {

      body: {
        query: {
          multi_match: {
            query: 'to be or not to be',

          }
        }
      },

        search_fields:{
          play_name:{},
          text_entry:{}
        },
        //Results display: name of the play, its speaker, text entry, and speech number.
        result_fields: {
            play_name: {
                snippet: {
                    size: 35,
                    fallback: true
                }
            },
            speaker: {
                snippet: {
                    size: 50,
                    fallback: true
                }
            },
            text_entry: {
                snippet: {
                    size: 70,
                    fallback: true
                }
            }
        }
    }
};

function App() {
    return (
        <SearchProvider 
        config={config}>

            <div className="App">
              <h1 className="heading">Shakespeare Work Library</h1>
                <Layout
                    header={
                    <SearchBox 
                      inputProps={{ placeholder: "type a character name or a text line"}}

                    />}

                    bodyContent={
                      <Results titleField = "play_name"/>}
                        bodyHeader={
                        <> 
                          <PagingInfo/> 
                          <ResultsPerPage options = {[5, 10, 20]}/> 
                        </>
                        }
                        bodyFooter={< Paging />}
                />
            </div>
        </SearchProvider>
    );
}
export default App;
