const App = () => {
  const [books, setBooks] = useState([])

  useEffect(() => {
    const fetchBooks = async () => {
      const res = await axios.get(
        `https://api.nytimes.com/svc/books/v3/lists/current/hardcover-fiction.json?api-key=${process.env.REACT_APP_BOOKS_API_KEY}`
      )
      setBooks(res.data.results.books)
      console.log(res.data.results.books)
    }

    fetchBooks()
  }, [])

  return (
    <>
      <h1 className="heading">
        Bestsellers
      </h1>
      <section className="">
        {books.map((book) => {
          const {
            author,
            book_image,
            buy_links,
            description,
            primary_isbn10,
            publisher,
            rank,
            title,
          } = book

          return (
            <article
              key={rank}
              className=""
            >
              <div>
                <img
                  src={book_image}
                  alt={title}
                  className=""
                />
              </div>

              <div>
                <h3 className="">{title}</h3>
                <p className="">{description}</p>
                <p>
                  <span className="font-bold">Author:</span> {author}
                </p>
              </div>

              <ul className="mb-4">
                <li>
                  <span className="font-bold">Publisher:</span> {publisher}
                </li>
                <li>
                  <span className="font-bold">ISBN:</span> {primary_isbn10}
                </li>
              </ul>

              <ul>
                <h3 className="font-bold text-xl">Buy Now:</h3>
                {buy_links.map((link) => {
                  const { name, url } = link
                  return (
                    <div key={name}>
                      <a
                        href={url}
                        className="flex items-center"
                        target="_blank"
                        rel="noopenner noreferrer"
                      >
                        {name} <BiLinkExternal className="ml-1" />
                      </a>
                    </div>
                  )
                })}
              </ul>
            </article>
          )
        })}
      </section>
    </>
  )
}

export default App;
