import React, {useState, useEffect} from "react";

function App() {

    const [items, setItems] = useState([]);
    const [search, setSearch] = useState("");

// fetching the data from API and store values 
   useEffect(() => {
    fetchItems();
   }, []);

   const fetchItems = async() => {
        const data = await fetch('http://cosmic-elastic.eu.ngrok.io/shakespeare/_search');

        const itemsObj = await data.json();
        const items = itemsObj.hits.hits;

        console.log(items);   

        setItems(items);
    }

    const handleChange = (search) => {
      let matches = []
      if (search.length > 0) {
        matches = items.filter(item => {
          const regex = new RegExp(`${search}`, 'gi');
          return item.text_entry.ma
        //  if (search === ''){
        //    return item
        //  } else if (item.text_entry.toLowerCase().includes(search.toLowerCase())){
        //    return item
        //  }
        }).map((item, key) => {
          return (
            <div>
              <p>{item.text_entry}</p>
            </div>
          )
        })
      }

      setSearch()
    }


    return (
        <div className="App">
            <h1 className="heading">Shakespeare Work Library</h1>
            <div>
                <input
                    type="text"
                    placeholder="Search for characters or plays"
                    onChange={event => handleChange(event.target.value)}
                    value={search}
                    />
            </div>
        </div>
    );
}
export default App;
