import React, {useState, useEffect} from "react";
import Card from "./Card";


function App() {

    const [items, setItems] = useState([]);
    const [search, setSearch] = useState("");

// fetching the data from API and store values 
   useEffect(() => {
    fetchItems();
   }, []);

   const fetchItems = async() => {
        const data = await fetch('http://cosmic-elastic.eu.ngrok.io/shakespeare/_search');
        const itemsObj = await data.json();
        const items = itemsObj.hits.hits;

        setItems(items);
    }

    return (
        <div className="App">
            <h1 className="heading">Shakespeare Work Library</h1>
            <div>
                <input
                    type="text"
                    placeholder="Search for characters or plays"
                    value={search}
                    onChange={(event) => setSearch(event.target.value)}
                    /> 
            </div>
            <div>
              <Card 

              />
            </div>
        </div>
    );
}
export default App;
