import React from "react";
import AppSearchAPIConnector from "@elastic/search-ui-app-search-connector";
import {PagingInfo, ResultsPerPage,Paging,SearchProvider,Results, SearchBox} from "@elastic/react-search-ui";
import { Layout } from "@elastic/react-search-ui-views";
//import ResultView from "./ResultView";
import "@elastic/react-search-ui-views/lib/styles/styles.css";



// retrieving credentials from my account
const connector = new AppSearchAPIConnector({
  searchKey: "search-j2upnixcxa1k28efrs8sugrv",
  engineName: "play-search",
  endpointBase: "https://play-search.ent.us-central1.gcp.cloud.es.io",
  cacheResponses: false
});

// setting configurations for connection and searches

const configurationOptions = {
  apiConnector: connector,

  // searching 
  searchQuery: {
      body: {
        "query": {  
          "match_phrase": {}
        }
      },
    result_fields: {
      play_name: {
        snippet: {
          size: 35, 
          fallback: true 
        }
      },
      speaker: {
        snippet: {
          size: 50,
          fallback: true
        }
      },
      text_entry: {
        snippet: {
          size: 70,
          fallback: true
        }
      },
      speech_number: {
        snippet: {
          size: 50,
          fallback: true
        }
      },
    },
  }
};

function App() {
  return (
    <SearchProvider config={configurationOptions}>
      <div className="App">
        <Layout
          header={<SearchBox />}
          bodyContent={
              <Results 
              titleField="play_name" 
              urlField="nps_link" 
              // resultView={ResultView}
              />
          }
          bodyHeader={
            <>
              <PagingInfo />
              <ResultsPerPage />
            </>
          }
          bodyFooter={
            <Paging />
          }
        />
      </div>
    </SearchProvider>
  );
}

export default App;
