import React, {useState, useEffect} from "react";
import CardList from "./CardList";
import SearchBox from './SearchBox';
import Pagination from './Pagination';
//import Pagination from '@mui/material/Pagination';
//import usePagination from "./Pagination";

function App() {

    // ============ setting the initial states for items and search ============
    const [items, setItems] = useState([]);
    const [search, setSearch] = useState("");

    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(10);

    // ============ fetching the data from API and store values ============
    useEffect(() => {fetchItems()}, []);

    const fetchItems = async() => {
        const data = await fetch('http://cosmic-elastic.eu.ngrok.io/shakespeare/_search?size=100');
        const itemsObj = await data.json();
        const items = itemsObj.hits.hits;

        // ============ store just fetched items in an array ============   
        setItems(items);
    }


    // ============ filtering the plays according to the input ============
    const filteredPlays = items.filter((play) => {
        return play._source.text_entry.toLowerCase().includes(search.toLowerCase());
    });


    // // ============ pagination ============
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = items.slice(indexOfFirstPost, indexOfLastPost);


    const [page, setPage] = useState(1);
    const perPage = 10;

    // const count = Math.ceil(items.length / perPage)
    // const DATA = usePagination(items, perPage)

    // const handleChange = (e, pg) => {
    //     setPage(pg);
    //     DATA.jump(pg);
    //   }; 


    // ============ rendering the App ============
    return !items.length ? ( 

    // renders a loading page while fetching data       
     <div className="loading-page"><h1 className="loading-page-text"> Loading, please wait... </h1></div> 
     ) : ( 

        <div className="App">
            <h1 className="heading">Shakespeare Work Library</h1>
            <div className="app-body">
                <SearchBox 
                    searchChange={(event) => setSearch(event.target.value)}
                />

              {/* {DATA.currentData().map(value => {
                    return />
                })} */}

                <CardList plays={filteredPlays} />
                 {/* <Pagination 
                count={count}
                size="large"
                page={page}
                variant="outlined"
                shape="rounded"
                onChange={handleChange}
                color="primary" 
                 />  */}
            </div>
        </div> 
    );
}
export default App;
